export default function ImageCentered({source, alt_str, wide, len}) {

    return (
        <div className="mt-16 flow-root sm:mt-24" width={wide} height={len} >
            <div className="-m-2 rounded-xl bg-gray-900/5 p-2 ring-1 ring-inset ring-gray-900/10 lg:-m-4 lg:rounded-2xl lg:p-4" width={wide} height={len}>
                <img
                src={source}
                alt={alt_str}
                width="100%"
                height="100%"
                className="rounded-md shadow-2xl ring-1 ring-gray-900/10"
                />
            </div>
        </div>
    );}
