import React from "react";
import ImageCentered from '../components/ImageCentered'
import FeatureList from "../components/FeatureList";
import ClientLogos from "../components/ClientLogos";
import Article from "../components/Article";
import Faq from "../components/Faq";
import ImageWithText from "../components/ImageWithText";
import Panel from "../components/Panel";

import {
  feats,
  tiers,
  faqs,
} from "./homeConfig"


const footerNavigation = {
  solutions: [
    { name: 'Hosting', href: '#' },
    { name: 'Data Services', href: '#' },
    { name: 'Uptime Monitoring', href: '#' },
    { name: 'Enterprise Services', href: '#' },
  ],
  support: [
    { name: 'Pricing', href: '#' },
    { name: 'Documentation', href: '#' },
    { name: 'Guides', href: '#' },
    { name: 'API Reference', href: '#' },
  ],
  company: [
    { name: 'About', href: '#' },
    { name: 'Blog', href: '#' },
    { name: 'Jobs', href: '#' },
    { name: 'Press', href: '#' },
    { name: 'Partners', href: '#' },
  ],
  legal: [
    { name: 'Claim', href: '#' },
    { name: 'Privacy', href: '#' },
    { name: 'Terms', href: '#' },
  ],
}

let references = [
    {source: "https://tailwindui.com/img/logos/158x48/transistor-logo-gray-900.svg", name: "Transistor"},
    {source: "https://tailwindui.com/img/logos/158x48/reform-logo-gray-900.svg", name: "Reform"},
    {source: "https://tailwindui.com/img/logos/158x48/tuple-logo-gray-900.svg", name: "Tuple"},
    {source: "https://tailwindui.com/img/logos/158x48/savvycal-logo-gray-900.svg", name: "SavvyCal"},
    {source: "https://tailwindui.com/img/logos/158x48/statamic-logo-gray-900.svg", name: "Statamic"}

];


export default function Home() {
  return (
    <div className="bg-white">
      <main className="isolate">

        {/* Hero section */}
        <Article art={{
                    head:"Deploy to the cloud with confidence", 
                    body:`Anim aute id magna aliqua ad ad non deserunt sunt. Qui irure qui lorem cupidatat commodo. Elit sunt
                    amet fugiat veniam occaecat fugiat aliqua.`}} 
                 button={{
                    exists:true, 
                    text:"Get started", 
                    href:"/"}} 
                style={{ marginTop:"10%", }}
        />

        <ImageCentered source={"https://tailwindui.com/img/component-images/project-app-screenshot.png"} alt_str={"App screenshot"} size={2} centered={true}/>

        {/* Logo cloud */}
        <ClientLogos logos={references} />

        {/* Feature section */}
        <FeatureList head={{
                        name: "Everything you need to deploy your app",
                        desc: "Quis tellus eget adipiscing convallis sit sit eget aliquet quis. Suspendisse eget egestas a elementum pulvinar et feugiat blandit at. In mi viverra elit nunc.",
                     }} 
                     features={feats} />

        {/* Testimonial section */}
        <ImageWithText info={{
                            imgSrc: "https://images.unsplash.com/photo-1601381718415-a05fb0a261f3?ixid=MXwxMjA3fDB8MHxwcm9maWxlLXBhZ2V8ODl8fHxlbnwwfHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1216&q=80",
                            head: "Name",
                            body: `“Amet amet eget scelerisque tellus sit neque faucibus non eleifend. Integer eu praesent at a. Ornare
                            arcu gravida natoque erat et cursus tortor consequat at. Vulputate gravida sociis enim nullam
                            ultricies habitant malesuada lorem ac.”`,
                            sig: "Judith Black",
                            name: "CEO of Tuple"
                        }} />

        {/* Pricing section */}
        <Article art={{
                    head:"Pricing plans for teams of all sizes", 
                    body:`Distinctio et nulla eum soluta et neque labore quibusdam. Saepe et quasi iusto modi velit ut non voluptas
                    in. Explicabo id ut laborum.`}} 
                 button={{
                    exists:false}} 
                style={{ marginTop:"10%", }}
        />

        <Panel tiers={tiers} />



        {/* FAQs */}
        <Faq faqs={faqs} style={{marginTop:"5%"}} />

        {/* CTA section */}
        <Article art={{
                    head:"Boost your productivity. Start using our app today.", 
                    body:`Incididunt sint fugiat pariatur cupidatat consectetur sit cillum anim id veniam aliqua proident excepteur
                    commodo do ea.`}} 
                 button={{
                    exists:false, 
                    text:"Get started", 
                    href:"/"}} 
        />

      </main>
    </div>
  )
}
