import { CheckIcon } from '@heroicons/react/20/solid'

function rendButton (option){
    if (option.button){
        return (<a href={option.href} aria-describedby={option.id} className='text-indigo-600 ring-1 ring-inset ring-indigo-200 hover:ring-indigo-300 mt-8 block rounded-md px-3 py-2 text-center text-sm font-semibold leading-6 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600'>
                    {option.buttonText}
                </a>)
    }} 


function rendPrice (option){
    if (option.price){
        return (<p className="mt-6 flex items-baseline gap-x-1">
                    <span className="text-4xl font-bold tracking-tight text-gray-900">{option.currency}{option.priceMonthly}</span>
                    <span className="text-sm font-semibold leading-6 text-gray-600">/{option.payFreq}</span>
                </p>)
    }}



export default function Panel({ tiers }) {
    return (
      <div className="mx-auto max-w-7xl px-6 lg:px-8">
        <div className="isolate mx-auto mt-16 grid max-w-md grid-cols-1 gap-y-8 sm:mt-20 lg:mx-0 lg:max-w-none lg:grid-cols-3">
            {tiers.map((tier, tierIdx) => (
            <div
                key={tier.id}
                className={
                [tier.mostPopular ? 'lg:z-10 lg:rounded-b-none rounded-3xl' : 'lg:mt-8',
                tierIdx === 0 ? 'lg:rounded-r-none rounded-3xl' : '',
                tierIdx === tiers.length - 1 ? 'lg:rounded-l-none rounded-3xl' : '',
                'flex flex-col justify-between bg-white p-8 ring-1 ring-gray-200 xl:p-10'].join(" ")
                }

            >
                <div>
                <div className="flex items-center justify-between gap-x-4">
                    <h3
                    id={tier.id}
                    className='text-gray-900 text-lg font-semibold leading-8'
                    >
                    {tier.name}
                    </h3>
                    {tier.mostPopular ? (
                    <p className="rounded-full bg-indigo-600/10 px-2.5 py-1 text-xs font-semibold leading-5 text-indigo-600">
                        Most popular
                    </p>
                    ) : null}
                </div>
                <p className="mt-4 text-sm leading-6 text-gray-600">{tier.description}</p>

                {rendPrice(tier)}
                <ul role="list" className="mt-8 space-y-3 text-sm leading-6 text-gray-600">
                    {tier.features.map((feature) => (
                    <li key={feature} className="flex gap-x-3">
                        <CheckIcon className="h-6 w-5 flex-none text-indigo-600" aria-hidden="true" />
                        {feature}
                    </li>
                    ))}
                </ul>
                </div>
                {rendButton(tier)}
            </div>
            ))}
        </div>
      </div>
    );}
