import React from "react";

//components
import Article from "../components/Article";
import TodoList from "../components/ToDoList";
import Form from "../components/Form";

const tasks = [
  {"complete":false,"due_date":null,"first_name":"John","id":"2","last_name":"Wayne","what_to_do":"pull ups?"},
  {"complete":true,"due_date":null,"first_name":"John","id":"2","last_name":"Wayne","what_to_do":"push ups?"}
  ]
let inputs = [
  {id:"what_to_do", text:"What To Do", type:"text", required:true},
  {id:"due_date", text:"Due Date", type:"date", required:false},
  ]

export default function Home() {
  return (
      <>
        {/* Hero section */}
        <Article art={{
                    head:"What to do today...", 
                    body:`Manage your tasks below`}} 
                 button={{
                    exists:false, 
                    text:"", 
                    href:"/"}} 
                style={{ marginTop:"10%", }}
        />

        <TodoList tasks={tasks} style={{ paddingLeft:"10%", paddingRight:"10%", marginTop:"5%", }}/>

        <Form inputs={inputs}/>
      </>
  )
}
