export default function ImageCentered({source, alt_str, size, centered}) {

    let d = [2, 4, 5, 6, 7];
    size = d[size-1];
    let pad = "0%";

    if (centered){
        centered = "auto";
    } else {
        centered = "left";
        pad = "2%";
    }

    return (
        <div className={`mx-`+centered+` max-w-`+size+`xl`} style={{paddingLeft: pad}}>
            <div className="mt-16 flow-root sm:mt-24" >
                <div className="-m-2 rounded-xl bg-gray-900/5 p-2 ring-1 ring-inset ring-gray-900/10 lg:-m-4 lg:rounded-2xl lg:p-4" >
                    <img
                    src={source}
                    alt={alt_str}
                    width="100%"
                    height="100%"
                    className="rounded-md shadow-2xl ring-1 ring-gray-900/10"
                    />
                </div>
            </div>
        </div>
    );}
