import React from "react";
import Article from "../components/Article";
import TodoList from "../components/ToDoList";
import axios from 'axios';
import { useState, useEffect } from "react";


export default function Home({users}) {

  function get(url){
    axios.get(url, {
      mode: 'no-cors',
      headers: {
        'Access-Control-Allow-Origin': '*',
        'Content-Type': 'application/json',}
    })
  }

  function getTasks() {

    fetch(window.host_addr+"/api/items", {mode:"no-cors"})
    .then(res=>res.json())
    .then(data=>setTasks(data))
  }

    const [tasks, setTasks] = useState([]);
    const [update, setUpdate] = useState(false);

    useEffect(()=>{
        getTasks()
    }, [update])

  return (
      <>
        {/* Hero section */}
        <Article art={{
                    head:"What to do today...", 
                    body:`Manage your tasks below`}} 
                 button={{
                    exists:false, 
                    text:"", 
                    href:"/"}} 
                style={{ marginTop:"10%", }}
        />

        <TodoList get={get} tasks={tasks} style={{ paddingLeft:"10%", paddingRight:"10%", marginTop:"5%", }}/>

        <div style={{marginTop:'5%'}}>
        <form style={{paddingLeft:'10%', paddingRight:'10%'}}> 
        <div className="space-y-12">

            <div className="border-b border-gray-900/10 pb-12">
            <h2 className="text-base font-semibold leading-7 text-gray-900">Add A User</h2>

            <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6 inline">

                <div className="sm:col-span-2">
                <label htmlFor="first-name" className="block text-sm font-medium leading-6 text-gray-900">
                    What To Do
                </label>
                <div className="mt-2">
                    <input
                    id="first-name"
                    name="first-name"
                    type="text"
                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                    />
                </div>
                </div>

                <div className="sm:col-span-2">
                <label htmlFor="last-name" className="block text-sm font-medium leading-6 text-gray-900">
                    Due Date
                </label>
                <div className="mt-2">
                    <input
                    id="last-name"
                    name="last-name"
                    type="text"
                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                    />
                </div>
                </div>

                <div className="sm:col-span-3">
                <label htmlFor="country" className="block text-sm font-medium leading-6 text-gray-900">
                  User
                </label>
                <div className="mt-2">
                  <select
                    id="user"
                    name="user"
                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:max-w-xs sm:text-sm sm:leading-6"
                  >
                    {users.map((user)=>{
                        return(<option key={user.uid} value={user.uid}>{user.first_name} {user.last_name}</option>)
                    })}

                  </select>
                  </div>
                  </div>


            </div>
            </div>
            </div>


        <div className="mt-6 flex items-center justify-end gap-x-6">

            <button
            type="submit"
            className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
            Save
            </button>
        </div>
        </form>
        </div>
      </>
  )
}
